import React from 'react'
import axios from 'axios'

const PostCreate = () => {
  const [title, setTitle] = React.useState('')
  const handleSubmit = e =>{
    e.preventDefault()
    console.log(title)
  }
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group"><label htmlFor="">Title</label>
        <input type="text" className="form-control" value={title} onChange={e=>setTitle({title:e.target.value})}/>
        </div>
        <button className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}

export default PostCreate
